$(function(){var a={audio:$("#bgTune"),playpause:$("#playpause-audio")};var b=a.audio[0];a.playpause.click(function(){if(b.paused){b.play();$(".sound_on").addClass("play-audio");$(".sound_off").removeClass("play-audio")}else{b.pause();$(".sound_off").addClass("play-audio");$(".sound_on").removeClass("play-audio")}})});